import React, { useState } from 'react';
import Job from './job.js';
import JobBoard from './jobBoard.js';
import './App.css';

//jobBoard




function Jobs({jobs}){
   console.log('job is', jobs[0]);

   const [open, setOpen] = React.useState(false);
   const [selectedJob, selectJob] = React.useState({});

function handleClose(){
    setOpen(false);
}

function handleClickOpen(){
    setOpen(false);
}
   return(
    <div className="jobs">
        <JobBoard open={open} job={selectedJob} handleClose={handleClose} />
        {jobs.map((job,i) => <Job key={i} job={job} onClick={()=>{
            console.log('clicked')
            handleClickOpen();
            selectJob();
            }} />)}
    </div>
   )
}

export default Jobs;
