import React, { useState } from 'react';
import Job from './job.js';
import JobBoard from './jobBoard.js';
import './App.css';

import Typography from '@material-ui/core/Typography';






function Jobs({jobs}){


   const [open, setOpen] = React.useState(false);
   const [selectedJobs, selectJob] = React.useState({});

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
  };



   return(
    <div className="jobs">
        <div>
            <Typography  component='h2' >Total number of jobs: {jobs.length} </Typography>
        </div>
        <JobBoard open={open} handleClose={handleClose} />
        <Typography>
            {jobs.map((job,i) => <Job key={i} job={job} onClick={()=>{
                handleClickOpen()
                selectJob(job)
                }} />)}
        </Typography>

    </div>
   )
}

export default Jobs;
