import Job from './job.js';
import JobBoard from './jobBoard.js';
import './App.css';

import Typography from '@material-ui/core/Typography';
import jobBoard from './jobBoard.js';





function Jobs({jobs}){
   console.log('job is', jobs[0]);


   const [open, setOpen] = React.useState(false);

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
  };



   return(
    <div className="jobs">
        <div>
            <Typography  component='h2' >Total number of jobs: {jobs.length} </Typography>
        </div>
        <JobBoard open={open} handleClose={handleClickOpen} onClick={onClick} />
        <Typography>
            {jobs.map((job,i) => <Job key={i} job={job} />)}
        </Typography>

    </div>
   )
}

export default Jobs;
