import React, { useState } from 'react';
import Job from './job.js';
import JobBoard from './jobBoard.js';
import './App.css';

//jobBoard
const [selectedJob, selectJob] = React.useState(false);
const [open, setOpen] = React.useState()

function Jobs({jobs}){
   console.log('job is', jobs[0]);
   return(
    <div className="jobs">
        <JobBoard open={open} job={selectedJob} handleClose={handleClose} />
        {jobs.map(job => <Job job={job} />)}
    </div>
   )
}

export default Jobs;
