import React, { useState } from 'react';
import Job from './job.js';
import JobBoard from './jobBoard.js';
import './App.css';

import Typography from '@material-ui/core/Typography';

//jobBoard




function Jobs({jobs}){
   console.log('job is', jobs[0]);

   const [open, setOpen] = React.useState(false);
   const [selectedJob, selectJob] = React.useState({});

function handleClose(){
    setOpen(false);
}

function handleClickOpen(){
    setOpen(false);
}
   return(
    <div className="jobs">
        <div>
            <Typography variant='h5' component='h1' >total number of jobs</Typography>
        </div>
        <JobBoard open={open} job={selectedJob} handleClose={handleClose} />
        <Typography>
            {jobs.map((job,i) => <Job key={i} job={job} onClick={()=>{
                console.log('clicked')
                handleClickOpen();
                selectJob();
            }} />)}
        </Typography>

    </div>
   )
}

export default Jobs;
